-- Count Records.lua-- Created with SQLiteManager 4.0.0-- On Sunday, October 28, 2012-- Ask user for a table nametableName = Dialogs:GetValue("Count Records", "Enter table name:")if (tableName == false) then return end-- Query databasers = Database:SQLSelect("SELECT count(*) FROM " .. tableName)if (rs == nil) then return end-- Show user the resultDialogs:Message("Table " .. tableName .. " has " .. rs:Value(1) .. " records.")